/* * Draughts - Free Version *  * Version: 1.0.0 * Copyright 2015 Galaa (www.galaa.mn). All Rights Reserved. * GNU/GPL License - http://www.gnu.org/copyleft/gpl.html *  */function draughts($table){$('div.draugths').each(function(){draw_board($(this));draw_piecies($(this));});}function draw_board($table){var $board = $table;$board.append($('<div/>').addClass('board'));$square_number = 1;for(var $row = 0; $row<10; $row++){for(var $col = 0; $col<10; $col++){if($row % 2 == 1 && $col % 2 === 0 || $row % 2 == 0 && $col % 2 === 1){$board.append($('<div/>').addClass('square dark '+$square_number).append($('<p/>').append($square_number)));$square_number ++;}else{$board.append($('<div/>').addClass('square light'));}}}}function draw_piecies($table){var $board = $table.find('.board');var $pieces = $table.data('pieces');$.each($pieces, function($key, $piece){if($piece.crowned === '1'){$board.append($('<div/>').addClass('piece piece-'+$piece.position+' piece-'+$piece.team).append($('<div/>').addClass('crown')));}else{$board.append($('<div/>').addClass('piece piece-'+$piece.position+' piece-'+$piece.team));}});}$(document).ready(function(){draughts();});
